import React from 'react'

const bookSection = ({data}) => {
    // console.log(data)
  return (
    <div className="d-flex justify-content-around align-items-center flex-wrap my-3 ">
    {data &&
      data.map((item, index) => (
       <div className='' style={{
        width:"200px",
        height:"350px",

        backgroundColor:"white"
       }}>
         <div>
            <img style={{width:"200px", height:"220px"}} className="img-fluid" src={item.image} alt="/" />
         </div>
         <h5 className='px-2 m-0 my-1'>{item.bookname.slice(0,15)}</h5>
        <b className='px-2 m-0'>Rs: {item.price}</b>
        <p className='px-2 m-0'>{item.description.slice(0,20)}</p>

        <div className='d-flex justify-content-around align-item-center'>
        <button type="button" class="btn btn-primary">Update</button>

        <button type="button" class="btn btn-primary">Delete</button>
        </div>

       </div>
      ))}
  </div>

  )
}

export default bookSection;
