const bookSection = ({data}) => {
    // console.log(data)
  return (
    <div classNames="d-flex justify-content-center align-items-center row">
        {data && data.map((item,index)=> 
       <div className='felx-column'>
                <div className="card" style={{width: "18rem;"}}>
                <div className="card-body">
                <h5 className="card-title">{item.bookname}</h5>
                <h6 className="card-subtitle mb-2 text-muted">{item.description} </h6>
                <p className="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
                <a href="#" className="card-link">Card link</a>
                <a href="#" className="card-link">Another link</a>
                </div>
                </div>
        </div>        

        )}
    </div>
  )
}

export default bookSection;
