import React, { useState } from 'react';
import axios from 'axios';

const UpdateBook = () => {
  const [Data, setData] = useState({
    bookname: '',
    author: '',
    description: '',
    image: '',
    price: '',
  });

  const change = (e) => {
    const { name, value } = e.target;
    setData({ ...Data, [name]: value });
  };

  console.log(Data);
 const Submit = async(e) =>{
      await axios.post("http://localhost:5000/api/b1/add",Data)
      .then((res)=>{
        alert(res.data.message,"book add succusesfully")
      })
 }
  return (
    <div className="bg-dark">
      {/* add book form */}
      <div
        style={{
          // justifyContent: 'center',
          // width: '50rem',
          // marginLeft: '20rem',
          // marginTop: '2rem',
        }}
      >
        <h3 className="text-white" style={{ marginLeft: '20rem' }}>
          Update Book   
        </h3>
        <form>
          <div className="form-group mb-4">
            <label className="mb-2 text-white" htmlFor="bookname">
              Book Name
            </label>
            <input 
              onChange={change}
              value={Data.bookname}
              name="bookname"
              type="text"
              className="form-control"
              id="bookname"
              placeholder="Enter Book name"
            />
          </div>

          <div className="form-group mb-4">
            <label className="mb-2 text-white" htmlFor="author">
              Author Name
            </label>
            <input 
              onChange={change}
              value={Data.author}
              name="author"
              type="text"
              className="form-control"
              id="author"
              placeholder="Enter author name"
            />
          </div>

          <div className="form-group mb-4">
            <label className="mb-2 text-white" htmlFor="description">
              Description
            </label>
            <input
              onChange={change}
              value={Data.description}
              name="description"
              type="text"
              className="form-control"
              id="description"
              placeholder="Enter Description"
            />
          </div>

          <div className="form-group mb-4">
            <label className="mb-2 text-white" htmlFor="image">
              Image
            </label>
            <input
              onChange={change}
              value={Data.image}
              name="image"
              type="text"
              className="form-control"
              id="image"
              placeholder="Enter Image URL"
            />
          </div>

          <div className="form-group mb-4">
            <label className="mb-2 text-white" htmlFor="price">
              Price
            </label>
            <input
              onChange={change}
              value={Data.price}
              name="price"
              type="text"
              className="form-control"
              id="price"
              placeholder="Enter Price"
            />
          </div>


          <div className='ml-2' style={{marginLeft:"2rem"}}>
          <button type="submit" className="btn btn-success mt-3 mb-5" onClick={Submit}>
            Update
          </button>
          </div>

        </form>
      </div>
    </div>
  );
};

export default UpdateBook;
