import React from "react";
import { Link, useNavigate } from "react-router-dom";

const BookSection = ({ data }) => {
  return (
    <div>
      <div className="container">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4">
          {data &&
            data.map((item, index) => (
              <div className="bg-white shadow-lg rounded-lg overflow-hidden" key={index}>
                <img
                  className="w-full h-48 object-cover object-center"
                  src={item.image}
                  alt="/"
                />
                <div className="p-4">
                  <h5 className="text-lg font-medium text-gray-900">{item.bookname.slice(0, 15)}</h5>
                  <p className="text-gray-700 mt-2">Rs: {item.price}</p>
                  <p className="text-gray-700 mt-2">{item.description.slice(0, 20)}</p>
                  <div className="mt-4 flex justify-between">
                    <Link to="/update">
                      <button className="px-4 py-2 bg-gray-200 text-gray-800 rounded hover:bg-gray-300">
                        Update
                      </button>
                    </Link>
                    <button className="px-4 py-2 bg-gray-200 text-gray-800 rounded hover:bg-gray-300">
                      Delete
                    </button>
                  </div>
                </div>
              </div>
            ))}
        </div>
      </div>
    </div>
  );
};

export default BookSection;
