import React from 'react'

const bookSection = ({data}) => {
    // console.log(data)
  return (
    <div className="container">
    <h4 className="text-center my-4">Book Section</h4>
    <div className="card-container" style={{ display: "flex"}}>
      {data.map((item) => (
        <div className="card" key={item._id}>
          <img src={item.image} className="card-img-top" alt={item.bookname} />
          <div className="card-body">
            <h5 className="card-title">{item.bookname}</h5>
            <h6 className="card-subtitle mb-2 text-muted">{item.author}</h6>
            <p className="card-text">{item.description}</p>
            <a href="#" className="btn btn-primary">
              Read More
            </a>
          </div>
        </div>
      ))}
    </div>
  </div>

  )
}

export default bookSection;
