import React, { useState, useEffect } from 'react'
import axios from 'axios';
import BookSection from "../pages/bookSection"
const Book = () => {

  const [Data, setData] = useState()

  useEffect(() => {
    const fetch = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/b1/getBook");
        setData(response.data.book);
        // console.log(response.data);
      } catch (error) {
        console.log(error);
      }
    };
    fetch();

  })
  return (
    <div className='row'>
        <div class="card" style={{width: "18rem"}} >
            <img class="card-img-top" src="https://cdn.pixabay.com/photo/2015/07/08/07/41/tree-835455_640.jpg" alt="Card image cap"/>
            <div class="card-body">
              <h5 class="card-title">Card title</h5>
              <p class="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
              <a href="#" class="btn btn-primary">Go somewhere</a>
            </div>
        </div>
      </div>
  )
}

export default Book;
