import React from "react";
import { Link, useNavigate } from "react-router-dom";

const BookSection = ({ data }) => {
  // console.log(data)
  return (
    <div>
      <div className="container">
        <div className="row">
          {data &&
            data.map((item, index) => (
              <div className="col-md-2 col-sm-4 mb-4" key={index}>
                <div className="card h-100">
                  <img
                    className="card-img-top"
                    src={item.image}
                    alt="/"
                    style={{ height: "200px", objectFit: "cover" }}
                  />
                  <div className="card-body">
                    <h5 className="card-title">{item.bookname.slice(0, 15)}</h5>
                    <p className="card-text">Rs: {item.price}</p>
                    <p className="card-text">{item.description.slice(0, 20)}</p>
                    <Link to="/update">
                      <button className="btn btn-secondary mr-2">Update</button>
                    </Link>
                    <button className="btn btn-secondary">Delete</button>
                  </div>
                </div>
              </div>
            ))}
        </div>
      </div>
    </div>
  );
};

export default BookSection;
