import React from "react";

const BookSection = ({ data }) => {
  // console.log(data)
  return (
    <div>
      <div className="container">
        <div className="row">
          {data &&
            data.map((item, index) => (
              <div className="col-md-3" key={index}>
                <div className="card" style={{ marginBottom: "1rem" ,width:"18rem" }}>
                  <img className="card-img-top" src={item.image} alt="/"  style={{height:"320px"}}/>
                  <div className="card-body">
                    <h5 className="card-title">{item.bookname.slice(0, 15)}</h5>
                    <p className="card-text">Rs: {item.price}</p>
                    <p className="card-text">{item.description.slice(0, 20)}</p>
                    <button href="#" className="btn btn-secondary">
                      Update
                    </button>
                    <button href="#" className="btn btn-secondary" style={{marginLeft}}>
                      Delete
                    </button>
                  </div>
                </div>
              </div>
            ))}
        </div>
      </div>
    </div>
  );
};

export default BookSection;
